package labwork;

public class Loops {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int count = 0;
		// Using while loop
		while (count < 10) {
			System.out.println("Hello world" + (count + 1));
			count++;

		}
		System.out.println(count);
		// using for loop
		for (int i = 0; i < 10; i++) {
			System.out.println(i);
		}
		
		}
	}
	

